// Validation commune AVANT Switch
const body = $input.first().json.body || $input.first().json;
const { action, data, user_id, channel } = body;

// Validation action
if (!action) {
  throw new Error('VALIDATION_ERROR: action is required');
}

// Validation user_id
if (!user_id) {
  throw new Error('VALIDATION_ERROR: user_id is required');
}

// Validation actions valides
const validActions = ['create', 'read', 'update', 'delete', 'list', 'close'];
if (!validActions.includes(action)) {
  throw new Error(`VALIDATION_ERROR: action must be one of: ${validActions.join(', ')}`);
}

// Ajouter metadata
return [{
  json: {
    action: action,
    data: data || {},
    user_id: user_id,
    channel: channel || 'api',
    _meta: {
      startTime: Date.now(),
      requestId: `${user_id}-${Date.now()}`
    }
  }
}];

// Action: create
const input = $input.first().json;
const { data, user_id, channel } = input;

// Validation data pour create
if (!data.user_id && !user_id) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.user_id or user_id is required for create',
      action: 'create'
    }
  }];
}

// Préparer données pour insertion
const conversationData = {
  user_id: data.user_id || user_id,
  channel: data.channel || channel || 'api',
  session_id: data.session_id || null,
  metadata: data.metadata || {}
};

return [{
  json: {
    success: true,
    action: 'create',
    conversationData: conversationData,
    user_id: user_id,
    channel: channel
  }
}];

// Action: read
const input = $input.first().json;
const { data, user_id } = input;

if (!data.conversation_id) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.conversation_id is required for read',
      action: 'read'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'read',
    conversation_id: data.conversation_id,
    user_id: user_id
  }
}];

// Action: update
const input = $input.first().json;
const { data, user_id } = input;

if (!data.conversation_id) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.conversation_id is required for update',
      action: 'update'
    }
  }];
}

// Préparer données pour update
const updateData = {
  conversation_id: data.conversation_id,
  metadata: data.metadata || null,
  session_id: data.session_id || null
};

return [{
  json: {
    success: true,
    action: 'update',
    updateData: updateData,
    user_id: user_id
  }
}];

// Action: delete
const input = $input.first().json;
const { data, user_id } = input;

if (!data.conversation_id) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.conversation_id is required for delete',
      action: 'delete'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'delete',
    conversation_id: data.conversation_id,
    user_id: user_id
  }
}];

// Action: list
const input = $input.first().json;
const { data, user_id } = input;

// Préparer filtres pour list
const filters = {
  user_id: data.user_id || user_id || null,
  channel: data.channel || null,
  limit: data.limit || 50,
  offset: data.offset || 0
};

return [{
  json: {
    success: true,
    action: 'list',
    filters: filters,
    user_id: user_id
  }
}];

// Action: close
const input = $input.first().json;
const { data, user_id } = input;

if (!data.conversation_id) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.conversation_id is required for close',
      action: 'close'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'close',
    conversation_id: data.conversation_id,
    user_id: user_id
  }
}];

// Format Response Standardisée
const input = $input.first().json;
const validation = $('Validate Input').first().json;
const startTime = validation._meta?.startTime || Date.now();

// Success case
if (input.success !== false && !input.isError) {
  return [{
    json: {
      success: true,
      action: validation.action,
      data: input.data || input,
      error: null,
      meta: {
        latency_ms: Date.now() - startTime,
        timestamp: new Date().toISOString(),
        request_id: validation._meta?.requestId
      }
    }
  }];
}

// Error case
return [{
  json: {
    success: false,
    action: validation.action,
    data: null,
    error: {
      code: input.errorCode || 'UNKNOWN_ERROR',
      message: input.errorMessage || input.message || 'An error occurred'
    },
    meta: {
      latency_ms: Date.now() - startTime,
      timestamp: new Date().toISOString(),
      request_id: validation._meta?.requestId
    }
  }
}];
