// Validation commune AVANT Switch
const body = $input.first().json.body || $input.first().json;
const { action, data, user_id, channel } = body;

// Validation action
if (!action) {
  throw new Error('VALIDATION_ERROR: action is required');
}

// Validation user_id
if (!user_id) {
  throw new Error('VALIDATION_ERROR: user_id is required');
}

// Validation actions valides
const validActions = ['create', 'read', 'update', 'delete', 'list', 'search', 'execute'];
if (!validActions.includes(action)) {
  throw new Error(`VALIDATION_ERROR: action must be one of: ${validActions.join(', ')}`);
}

// Ajouter metadata
return [{
  json: {
    action: action,
    data: data || {},
    user_id: user_id,
    channel: channel || 'api',
    _meta: {
      startTime: Date.now(),
      requestId: `${user_id}-${Date.now()}`
    }
  }
}];

// Action: create
const input = $input.first().json;
const { data, user_id } = input;

// Validation
if (!data.server_name) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.server_name is required for create',
      action: 'create'
    }
  }];
}

if (!data.tool_name) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.tool_name is required for create',
      action: 'create'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'create',
    toolData: {
      server_name: data.server_name,
      tool_name: data.tool_name,
      description_short: data.description_short || null,
      description_full: data.description_full || null,
      category: data.category || null,
      parameters: data.parameters || {},
      examples: data.examples || []
    },
    user_id: user_id
  }
}];

// Action: read
const input = $input.first().json;
const { data, user_id } = input;

if (!data.server_name || !data.tool_name) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.server_name and data.tool_name are required for read',
      action: 'read'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'read',
    server_name: data.server_name,
    tool_name: data.tool_name,
    user_id: user_id
  }
}];

// Action: update
const input = $input.first().json;
const { data, user_id } = input;

if (!data.server_name || !data.tool_name) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.server_name and data.tool_name are required for update',
      action: 'update'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'update',
    toolData: {
      server_name: data.server_name,
      tool_name: data.tool_name,
      description_short: data.description_short || null,
      description_full: data.description_full || null,
      category: data.category || null,
      parameters: data.parameters || null,
      examples: data.examples || null
    },
    user_id: user_id
  }
}];

// Action: delete
const input = $input.first().json;
const { data, user_id } = input;

if (!data.server_name || !data.tool_name) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.server_name and data.tool_name are required for delete',
      action: 'delete'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'delete',
    server_name: data.server_name,
    tool_name: data.tool_name,
    user_id: user_id
  }
}];

// Action: list
const input = $input.first().json;
const { data, user_id } = input;

const filters = {
  server_name: data.server_name || null,
  category: data.category || null,
  limit: data.limit || 50,
  offset: data.offset || 0
};

return [{
  json: {
    success: true,
    action: 'list',
    filters: filters,
    user_id: user_id
  }
}];

// Action: search
const input = $input.first().json;
const { data, user_id } = input;

if (!data.query) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.query is required for search',
      action: 'search'
    }
  }];
}

const searchParams = {
  query: data.query,
  category: data.category || null,
  limit: data.limit || 10
};

return [{
  json: {
    success: true,
    action: 'search',
    searchParams: searchParams,
    user_id: user_id
  }
}];

// Action: execute (track usage)
const input = $input.first().json;
const { data, user_id } = input;

if (!data.server_name || !data.tool_name) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'data.server_name and data.tool_name are required for execute',
      action: 'execute'
    }
  }];
}

return [{
  json: {
    success: true,
    action: 'execute',
    server_name: data.server_name,
    tool_name: data.tool_name,
    user_id: user_id
  }
}];

// Format Response Standardisée
const input = $input.first().json;
const validation = $('Validate Input').first().json;
const startTime = validation._meta?.startTime || Date.now();

// Success case
if (input.success !== false && !input.isError) {
  return [{
    json: {
      success: true,
      action: validation.action,
      data: input.data || input,
      error: null,
      meta: {
        latency_ms: Date.now() - startTime,
        timestamp: new Date().toISOString(),
        request_id: validation._meta?.requestId
      }
    }
  }];
}

// Error case
return [{
  json: {
    success: false,
    action: validation.action,
    data: null,
    error: {
      code: input.errorCode || 'UNKNOWN_ERROR',
      message: input.errorMessage || input.message || 'An error occurred'
    },
    meta: {
      latency_ms: Date.now() - startTime,
      timestamp: new Date().toISOString(),
      request_id: validation._meta?.requestId
    }
  }
}];
