const request = $input.first().json;

const companyName = request.company_name;
const siret = request.siret || null;
const country = request.country || 'FR';

// Generate research ID
const researchId = `OSINT-${Date.now()}`;

// Define data sources to query
const sources = [
  { name: 'Pappers', type: 'registry', enabled: country === 'FR' },
  { name: 'Societe.com', type: 'registry', enabled: country === 'FR' },
  { name: 'LinkedIn', type: 'social', enabled: true },
  { name: 'Google', type: 'search', enabled: true },
  { name: 'WHOIS', type: 'dns', enabled: true },
  { name: 'Crunchbase', type: 'business', enabled: true }
];

return {
  research_id: researchId,
  company_name: companyName,
  siret,
  country,
  sources: sources.filter(s => s.enabled),
  requested_by: request.requested_by || 'api',
  started_at: new Date().toISOString()
};

const researchId = $('Prepare Research').first().json.research_id;
const companyName = $('Prepare Research').first().json.company_name;

// Get results from different sources
const pappersData = $('Fetch Pappers Data').first()?.json || {};
const googleData = $('Google Search').first()?.json || {};

// Compile company profile
const profile = {
  research_id: researchId,
  company_name: companyName,

  // Legal info from Pappers
  legal_name: pappersData.nom_entreprise || null,
  siret: pappersData.siret || null,
  siren: pappersData.siren || null,
  legal_form: pappersData.forme_juridique || null,
  capital: pappersData.capital || null,
  creation_date: pappersData.date_creation || null,
  naf_code: pappersData.code_naf || null,
  naf_label: pappersData.libelle_code_naf || null,

  // Address
  address: pappersData.siege?.adresse_ligne_1 || null,
  postal_code: pappersData.siege?.code_postal || null,
  city: pappersData.siege?.ville || null,

  // Directors
  directors: pappersData.representants?.map(r => ({
    name: `${r.prenom || ''} ${r.nom || ''}`.trim(),
    role: r.qualite || 'Unknown',
    birth_date: r.date_naissance || null
  })) || [],

  // Financial data
  revenue: pappersData.chiffre_affaires || null,
  employees: pappersData.effectif || null,

  // Web presence from Google
  web_results: googleData.items?.slice(0, 5).map(item => ({
    title: item.title,
    url: item.link,
    snippet: item.snippet
  })) || [],

  // Metadata
  data_sources: ['Pappers', 'Google'],
  compiled_at: new Date().toISOString()
};

return profile;
