const request = $input.first().json;

const firstName = request.first_name;
const lastName = request.last_name;
const company = request.company || null;
const birthYear = request.birth_year || null;

// Generate profile ID
const profileId = `DIR-${Date.now()}`;

return {
  profile_id: profileId,
  first_name: firstName,
  last_name: lastName,
  full_name: `${firstName} ${lastName}`,
  company,
  birth_year: birthYear,
  requested_by: request.requested_by || 'api',
  started_at: new Date().toISOString()
};

const search = $('Prepare Director Search').first().json;
const pappersResults = $('Search Pappers Directors').first()?.json?.resultats || [];
const googleResults = $('Google LinkedIn Search').first()?.json?.items || [];

// Find matching director in Pappers
const matchingDirectors = pappersResults.filter(d => {
  const nameMatch = d.nom?.toLowerCase().includes(search.last_name.toLowerCase());
  const firstNameMatch = d.prenom?.toLowerCase().includes(search.first_name.toLowerCase());
  return nameMatch && firstNameMatch;
});

// Extract companies where person is director
const companies = [];
for (const director of matchingDirectors) {
  if (director.entreprises) {
    for (const entreprise of director.entreprises) {
      companies.push({
        name: entreprise.nom_entreprise,
        siren: entreprise.siren,
        role: entreprise.qualite,
        start_date: entreprise.date_prise_de_poste,
        active: entreprise.actif
      });
    }
  }
}

// Extract LinkedIn profiles from Google results
const linkedinProfiles = googleResults
  .filter(r => r.link?.includes('linkedin.com/in/'))
  .map(r => ({
    url: r.link,
    title: r.title,
    snippet: r.snippet
  }));

// Build profile
const profile = {
  profile_id: search.profile_id,
  full_name: search.full_name,
  first_name: search.first_name,
  last_name: search.last_name,

  // Director positions
  companies_count: companies.length,
  active_positions: companies.filter(c => c.active).length,
  companies,

  // Online presence
  linkedin_profiles: linkedinProfiles,
  has_linkedin: linkedinProfiles.length > 0,

  // Metadata
  data_quality: matchingDirectors.length > 0 ? 'high' : 'low',
  sources_used: ['Pappers', 'Google'],
  compiled_at: new Date().toISOString()
};

return profile;
