// Parse action L2 request
const input = $input.first().json.data || $input.first().json;

if (!input.query) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'query is required'
    }
  }];
}

return [{
  json: {
    query: input.query,
    rag_context: input.rag_context || '',
    rag_count: input.rag_count || 0,
    user_id: input.user_id || 'system',
    channel: input.channel || 'api',
    conversation_id: input.conversation_id || null
  }
}];

// Format response
const result = $input.first().json;

return [{
  json: {
    success: true,
    data: result
  }
}];
