// Parse error payload
const body = $input.item.json.body || $input.item.json;
const error = body.error || {};

return {
  json: {
    workflow_id: body.workflow_id || 'unknown',
    node_id: body.node_id || 'unknown',
    error_message: error.message || error.toString() || 'Unknown error',
    error_code: error.code || 'UNKNOWN',
    error_stack: error.stack || null,
    context: body.context || {},
    severity: body.severity || 'error',
    timestamp: new Date().toISOString()
  }
};

// Format Slack alert for critical errors
const error = $('Parse Error').item.json;

return {
  json: {
    channel: '#alerts-critical',
    username: 'IANA Error Bot',
    icon_emoji: ':fire:',
    text: `ðŸš¨ *CRITICAL ERROR in ${error.workflow_id}*`,
    attachments: [
      {
        color: 'danger',
        fields: [
          {
            title: 'Error Message',
            value: error.error_message,
            short: false
          },
          {
            title: 'Node ID',
            value: error.node_id,
            short: true
          },
          {
            title: 'Error Code',
            value: error.error_code,
            short: true
          },
          {
            title: 'Timestamp',
            value: error.timestamp,
            short: false
          }
        ]
      }
    ]
  }
};

// Simulate Slack send (will be replaced by real Slack node)
const message = $input.item.json;

return {
  json: {
    slack_sent: true,
    channel: message.channel,
    timestamp: new Date().toISOString()
  }
};

// Merge results and format response
const errorLog = $('Log Error to DB').item.json;
const slackSent = $input.item.json?.slack_sent || false;

return {
  json: {
    success: true,
    error_id: errorLog.error_id || errorLog[0]?.error_id,
    logged: true,
    alert_sent: slackSent,
    timestamp: new Date().toISOString()
  }
};
