// Parse RAG enrich request
const input = $input.first().json.data || $input.first().json;

if (!input.doc_id) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'doc_id is required'
    }
  }];
}

return [{
  json: {
    doc_id: input.doc_id,
    chunk_size: input.chunk_size || 1000,
    overlap: input.overlap || 200,
    generate_embeddings: input.generate_embeddings !== false
  }
}];

// Format response
const result = $input.first().json;
const parseData = $('Parse Input').first().json;

return [{
  json: {
    success: true,
    data: {
      doc_id: parseData.doc_id,
      chunk_count: result.chunk_count,
      chunks_created: true,
      embeddings_pending: parseData.generate_embeddings
    }
  }
}];
