// Parse RAG update request
const input = $input.first().json.data || $input.first().json;

if (!input.doc_id) {
  return [{
    json: {
      success: false,
      errorCode: 'VALIDATION_ERROR',
      errorMessage: 'doc_id is required'
    }
  }];
}

return [{
  json: {
    doc_id: input.doc_id,
    title: input.title || null,
    content: input.content || null,
    category: input.category || null,
    metadata: input.metadata || null,
    updated_by: input.updated_by || 'system'
  }
}];

// Format response
const result = $input.first().json;

return [{
  json: {
    success: true,
    data: result
  }
}];
